import React, { useContext } from "react";
import "../styles/products.css";
import { CartContext } from "../components/CartContext";

import { gsap } from "gsap";

/**
 * Animaci√≥n m√°gica de m√∫ltiples part√≠culas hacia un √≠cono objetivo.
 * @param {HTMLElement} buttonRef - Elemento desde donde parte la animaci√≥n (bot√≥n).
 * @param {HTMLElement} targetRef - Elemento hacia donde van las part√≠culas (√≠cono en el header).
 */
export const animateToCart = (buttonRef, targetRef) => {
  if (!buttonRef || !targetRef) return;

  const buttonRect = buttonRef.getBoundingClientRect();
  const targetRect = targetRef.getBoundingClientRect();

  // Coordenadas hacia el centro del target
  const targetX = targetRect.left + targetRect.width / 2;
  const targetY = targetRect.top + targetRect.height / 2;

  const particlesCount = 8; // üî• Cambia el n√∫mero de part√≠culas m√°gicas

  for (let i = 0; i < particlesCount; i++) {
    const particle = document.createElement("div");
    particle.className = "particle";
    document.body.appendChild(particle);

    // Posici√≥n inicial en el centro del bot√≥n
    particle.style.position = "absolute";
    particle.style.left = `${buttonRect.left + buttonRect.width / 2}px`;
    particle.style.top = `${buttonRect.top + buttonRect.height / 2}px`;

    // Peque√±a variaci√≥n para que no vayan todas iguales (magia!)
    const randomOffsetX = (Math.random() - 0.5) * 60; // dispersi√≥n horizontal
    const randomOffsetY = (Math.random() - 0.5) * 60; // dispersi√≥n vertical
    const randomDelay = Math.random() * 0.3; // unas salen antes, otras despu√©s
    const randomScale = 0.5 + Math.random() * 0.8;

    gsap.to(particle, {
      duration: 1.2 + Math.random() * 0.3,
      delay: randomDelay,
      x: targetX - buttonRect.left + randomOffsetX,
      y: targetY - buttonRect.top + randomOffsetY,
      scale: randomScale,
      opacity: 0.3 + Math.random() * 0.7,
      rotation: Math.random() * 360,
      ease: "power2.inOut",
      onComplete: () => particle.remove(),
    });
  }
};


const ProductCard = ({ product, role, user, onDelete }) => {
  const priceCOP = product.priceGalleons * 5000;
  const isMagical = role === "admin" || role === "usuario_magico";
  const isForbidden = product.forbidden && !isMagical;
  const { addToCart } = useContext(CartContext);

  const handleAddToCart = (e, product) => {
    if (!user) {
      alert("Debes iniciar sesi√≥n para agregar productos al carrito.");
      return;
    }

    addToCart(product);

    // ‚úÖ Animaci√≥n hacia el carrito
    const cartIcon = document.querySelector("#cart-icon");
    if (cartIcon) {
      animateToCart(e.currentTarget, cartIcon);
    }
  };

  return (
    <div className={`product-card ${product.category.replace(/\s+/g, "-").toLowerCase()}`}>
      <p style={{ fontSize: "0.8rem", color: "#999" }}>Rol: {role}</p>

      <img
        src={product.image}
        alt={product.name}
        className={isForbidden ? "product-image blurred" : "product-image"}
      />
      <h3>{product.name}</h3>
      <p>{isForbidden ? "üîí Producto restringido" : product.description}</p>

      <p className="price">
        {product.priceGalleons}‚ö±Ô∏è / ${priceCOP.toLocaleString()} COP
      </p>

      {!isForbidden && <p className="stock">Stock: {product.stock ?? 0}</p>}

      {!isForbidden && (
        <button className="btn-add" onClick={(e) => handleAddToCart(e, product)}>
          Agregar al carrito
        </button>
      )}

      {role === "admin" && !product.original && onDelete && (
        <button
          className="btn-add"
          style={{ marginTop: "0.5rem", backgroundColor: "#460b0b" }}
          onClick={() => {
            if (window.confirm("¬øEst√°s seguro de eliminar este producto?")) {
              onDelete(product._id || product.id);
            }
          }}
        >
          Eliminar producto
        </button>
      )}
    </div>
  );
};

export default ProductCard;
