import React, { useContext, useState } from "react";
import "../styles/products.css";
import { CartContext } from "../components/CartContext";
import animateToCart from "../styles/animateToCart";

const ProductCard = ({ product, role, user, onDelete, onUpdate }) => {
  const priceCOP = product.priceGalleons * 5000;
  const isMagical = role === "admin" || role === "usuario_magico";
  const isForbidden = product.forbidden && !isMagical;
  const { addToCart } = useContext(CartContext);

  // ‚úÖ Estado para edici√≥n
  const [isEditing, setIsEditing] = useState(false);
  const [editedProduct, setEditedProduct] = useState({
    name: product.name,
    description: product.description,
    priceGalleons: product.priceGalleons,
    stock: product.stock,
    image: product.image
  });

  const handleAddToCart = (e, product) => {
    if (!user) {
      alert("Debes iniciar sesi√≥n para agregar productos al carrito.");
      return;
    }

    addToCart(product);

    // ‚úÖ Animaci√≥n hacia el carrito
    const cartIcon = document.querySelector("#cart-icon");
    if (cartIcon) {
      animateToCart(e.currentTarget, cartIcon);
    }
  };

  const handleSave = () => {
    if (onUpdate) {
      onUpdate(product._id || product.id, editedProduct);
    }
    setIsEditing(false);
  };

  return (
    <div className={`product-card ${product.category.replace(/\s+/g, "-").toLowerCase()}`}>
      <p style={{ fontSize: "0.8rem", color: "#999" }}>Rol: {role}</p>

      {/* ‚úÖ Si est√° editando, se muestra un input para la imagen */}
      {isEditing ? (
        <input
          type="text"
          value={editedProduct.image}
          onChange={(e) => setEditedProduct({ ...editedProduct, image: e.target.value })}
          className="form-control mb-2"
          placeholder="URL de la imagen"
        />
      ) : (
        <img
          src={product.image}
          alt={product.name}
          className={isForbidden ? "product-image blurred" : "product-image"}
        />
      )}

      {isEditing ? (
        <>
          <input
            type="text"
            value={editedProduct.name}
            onChange={(e) => setEditedProduct({ ...editedProduct, name: e.target.value })}
            className="form-control mb-2"
          />
          <textarea
            value={editedProduct.description}
            onChange={(e) => setEditedProduct({ ...editedProduct, description: e.target.value })}
            className="form-control mb-2"
          />
          <input
            type="number"
            value={editedProduct.priceGalleons}
            onChange={(e) => setEditedProduct({ ...editedProduct, priceGalleons: parseFloat(e.target.value) })}
            className="form-control mb-2"
          />
          <input
            type="number"
            value={editedProduct.stock}
            onChange={(e) => setEditedProduct({ ...editedProduct, stock: parseInt(e.target.value) })}
            className="form-control mb-2"
          />
        </>
      ) : (
        <>
          <h3>{product.name}</h3>
          <p>{isForbidden ? "üîí Producto restringido" : product.description}</p>
          <p className="price">
            {product.priceGalleons}‚ö±Ô∏è / ${priceCOP.toLocaleString()} COP
          </p>
          {!isForbidden && <p className="stock">Stock: {product.stock ?? 0}</p>}
        </>
      )}

      {/* Botones */}
      {!isForbidden && !isEditing && (
        <button className="btn-add" onClick={(e) => handleAddToCart(e, product)}>
          Agregar al carrito
        </button>
      )}

      {role === "admin" && !product.original && (
        <div style={{ marginTop: "0.5rem" }}>
          {isEditing ? (
            <>
              <button
                className="btn-add"
                style={{ backgroundColor: "#1b5e20", marginRight: "0.5rem" }}
                onClick={handleSave}
              >
                ‚úÖ Guardar
              </button>
              <button
                className="btn-add"
                style={{ backgroundColor: "#9e9e9e" }}
                onClick={() => setIsEditing(false)}
              >
                ‚ùå Cancelar
              </button>
            </>
          ) : (
            <>
              <button
                className="btn-add"
                style={{ backgroundColor: "#0d47a1", marginRight: "0.5rem" }}
                onClick={() => setIsEditing(true)}
              >
                ‚úèÔ∏è Editar
              </button>
              <button
                className="btn-add"
                style={{ backgroundColor: "#460b0b" }}
                onClick={() => {
                  if (window.confirm("¬øEst√°s seguro de eliminar este producto?")) {
                    onDelete(product._id || product.id);
                  }
                }}
              >
                üóë Eliminar
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default ProductCard;
