import React, { useContext, useState } from "react";
import CartItem from "../components/CartItem";
import { AuthContext } from "../components/auth";
import axios from "axios";
import "../styles/Cart.css";
import { CartContext } from "../components/CartContext";
import { gsap } from "gsap";

const animateOrderSuccess = () => {
  const particlesCount = 30;
  const colors = ["#ffd700", "#ff8c00", "#ffffff", "#87ceeb"];

  for (let i = 0; i < particlesCount; i++) {
    const particle = document.createElement("div");
    particle.className = "particle";
    document.body.appendChild(particle);

    particle.style.position = "fixed";
    particle.style.left = `${window.innerWidth / 2}px`;
    particle.style.top = `${window.innerHeight / 2}px`;
    particle.style.width = "8px";
    particle.style.height = "8px";
    particle.style.borderRadius = "50%";
    particle.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
    particle.style.zIndex = "9999";
    particle.style.pointerEvents = "none";

    const angle = Math.random() * Math.PI * 2;
    const distance = 80 + Math.random() * 120;

    gsap.to(particle, {
      x: Math.cos(angle) * distance,
      y: Math.sin(angle) * distance,
      scale: 0.5,
      opacity: 0,
      duration: 1.2 + Math.random(),
      ease: "power2.out",
      onComplete: () => particle.remove(),
    });
  }

  // ‚úÖ Peque√±o "zoom m√°gico" como si se activara un hechizo
  gsap.fromTo(
    ".cart-summary-panel",
    { scale: 1 },
    { scale: 1.1, duration: 0.2, yoyo: true, repeat: 1, ease: "power1.inOut" }
  );
};

const GALEON_TO_COP = 24000;

const CartPage = () => {



  const { user, setUser } = useContext(AuthContext);
  const { cartItems, removeFromCart, clearCart, updateQuantity } = useContext(CartContext);
  const [address, setAddress] = useState("");

  const toGalleons = (cop) => Math.round(cop / GALEON_TO_COP);
  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const totalCOP = total * 24000;
  const handleRemoveItem = (_id) => {
    removeFromCart(_id);
  };

  const handleQuantityChange = (_id, amount) => {
    updateQuantity(_id, amount);
  };

  const handleCheckout = async () => {
    if (!address.trim()) {
      alert("Debes ingresar una direcci√≥n de env√≠o.");
      return;
    }

    const token = localStorage.getItem("token");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/orders",
        {
          items: cartItems,
          address,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      animateOrderSuccess();


      setTimeout(() => {
        alert("¬°Pedido enviado por lechuza! ü¶â");
      }, 500); // para que no corte la animaci√≥n
      clearCart();
      setAddress("");
      setUser(res.data.updatedUser);
    } catch (err) {
      console.error("Error al crear pedido:", err.response?.data || err);
      alert(err.response?.data?.error || "Error al procesar pedido.");
    }
  };

  return (
    <div className="cart-container">
      <div className="cart-items-panel">
        <h2>Productos en el carrito</h2>
        {cartItems.length === 0 ? (
          <p>Tu carrito est√° vac√≠o.</p>
        ) : (
          cartItems.map((item) => (
            <CartItem
              key={item._id}
              item={item}
              toGalleons={toGalleons}
              onRemove={handleRemoveItem}
              onQuantityChange={handleQuantityChange}
            />
          ))
        )}
      </div>

      <div className="cart-summary-panel">
        <h3>Resumen de compra</h3>
        <div className="summary-row">
          <span>Total:</span>
          <span>
            üí∞ {total} Galeones / ${totalCOP.toLocaleString()} COP
          </span>
        </div>


        <input
          type="text"
          className="magic-input"
          placeholder="Direcci√≥n de env√≠o (por ejemplo: Calle 9¬æ, Londres)"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />

        <button
          className="checkout-button"
          disabled={cartItems.length === 0}
          onClick={handleCheckout}
        >
          Realizar pedido
        </button>
      </div>
    </div>
  );
};

export default CartPage;
