import React, { useContext } from "react";
import "../styles/products.css";
import { CartContext } from "../components/CartContext";
import { gsap } from "gsap";

const animateToCart = (buttonRef, cartIconRef) => {
  const buttonRect = buttonRef.getBoundingClientRect();
  const cartRect = cartIconRef.getBoundingClientRect();

  const particle = document.createElement("div");
  particle.className = "particle";
  document.body.appendChild(particle);

  particle.style.position = "absolute";
  particle.style.left = `${buttonRect.left + buttonRect.width / 2}px`;
  particle.style.top = `${buttonRect.top + buttonRect.height / 2}px`;

  gsap.to(particle, {
    duration: 1,
    x: cartRect.left - buttonRect.left,
    y: cartRect.top - buttonRect.top,
    scale: 0.2,
    opacity: 0.5,
    ease: "power2.inOut",
    onComplete: () => particle.remove(),
  });
};

const ProductCard = ({ product, role, user, onDelete }) => {
  const priceCOP = product.priceGalleons * 5000;
  const isMagical = role === "admin" || role === "usuario_magico";
  const isForbidden = product.forbidden && !isMagical;
  const { addToCart } = useContext(CartContext);

  const handleAddToCart = () => {
    if (!user) {
      alert("Debes iniciar sesión para agregar productos al carrito.");
      return;
    }
    addToCart(product);
  };

  return (
    <div className={`product-card ${product.category.replace(/\s+/g, "-").toLowerCase()}`}>
      <p style={{ fontSize: "0.8rem", color: "#999" }}>Rol: {role}</p>

      <img
        src={product.image}
        alt={product.name}
        className={isForbidden ? "product-image blurred" : "product-image"}
      />
      <h3>{product.name}</h3>
      <p>{isForbidden ? "🔒 Producto restringido" : product.description}</p>

      <p className="price">
        {product.priceGalleons}⚱️ / ${priceCOP.toLocaleString()} COP
      </p>

      {!isForbidden && (
        <p className="stock">Stock: {product.stock ?? 0}</p>
      )}

      {!isForbidden && (
        <button
          className="btn-add"
          onClick={handleAddToCart}
        >
          Agregar al carrito
        </button>
      )}

      {role === "admin" && !product.original && onDelete && (
        <button
          className="btn-add"
          style={{ marginTop: "0.5rem", backgroundColor: "#460b0b" }}
          onClick={() => {
            if (window.confirm("¿Estás seguro de eliminar este producto?")) {
              onDelete(product._id || product.id);
            }
          }}
        >
          Eliminar producto
        </button>
      )}
    </div>
  );
};

export default ProductCard;
