import { createContext, useState, useEffect, useContext } from "react";
import axios from "axios";
import { CartContext } from "./CartContext";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // { username, role, money, ... }
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const { clearCart } = useContext(CartContext); // âœ… Para limpiar carrito

  const fetchUser = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      setIsLoggedIn(false);
      setUser(null);
      clearCart(); // âœ… Limpia carrito si no hay sesiÃ³n
      return;
    }

    try {
      const res = await axios.get("http://localhost:5000/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(res.data);
      setIsLoggedIn(true);

      // âœ… Si el usuario es invitado, limpiamos tambiÃ©n
      if (res.data.role === "invitado") {
        clearCart();
        console.log("âœ¨ Carrito limpiado: invitado detectado, no se guarda evidencia mÃ¡gica.");
      }
    } catch (err) {
      console.error("Error al obtener usuario:", err);
      setIsLoggedIn(false);
      setUser(null);
      localStorage.removeItem("token");
      clearCart();
    }
  };

  const login = (userData, token) => {
    localStorage.setItem("token", token);
    setUser(userData);
    setIsLoggedIn(true);

    if (userData.role === "invitado") {
      clearCart();
      console.log("âœ¨ Carrito limpiado: invitado detectado.");
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
    setIsLoggedIn(false);
    clearCart();
    alert("ðŸ”® Un hechizo anti-rastros ha borrado tus compras. No queda evidencia mÃ¡gica.");
  };

  useEffect(() => {
    fetchUser();
  }, []);

  return (
    <Auth.Provider value={{ user, setUser, isLoggedIn, login, logout, fetchUser }}>
      {children}
    </Auth.Provider>
  );
};
