import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "../styles/login.css";
import TermsModal from '../components/TermsModal';

const LoginPage = ({ onLoginSuccess }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showTerms, setShowTerms] = useState(false);
  const [acceptedTerms, setAcceptedTerms] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    setError('');

    if (!acceptedTerms) {
      setError('Debes aceptar los T√©rminos y Condiciones.');
      return;
    }
    if (username === 'admin' && password === 'admin') {
      onLoginSuccess(username);
      navigate('/'); // üîÅ Aqu√≠ redirige a la p√°gina principal
    } else {
      setError('Usuario o contrase√±a incorrectos.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <img src="/Sortilegios_Weasley_Cropped.png" alt="Logo" className="login-logo" />
        <h2>Iniciar Sesi√≥n</h2>
        <form onSubmit={handleLogin}>
          {/* ... campos del formulario ... */}
          <div className="form-group d-flex align-items-center">
            <input
              type="checkbox"
              id="terms"
              checked={acceptedTerms}
              onChange={(e) => setAcceptedTerms(e.target.checked)}
            />
            <label htmlFor="terms" className="ms-2">
              Acepto los{' '}
              <button type="button" onClick={() => setShowTerms(true)} className="btn btn-link p-0">
                T√©rminos y Condiciones
              </button>
            </label>
          </div>

          {error && <p className="error-message">{error}</p>}
          <button type="submit" className="login-btn">Acceder</button>
        </form>

        {/* Modal de T√©rminos */}
        {showTerms && (
          <TermsModal forceOpen={true} onClose={() => setShowTerms(false)} />
        )}
      </div>
    </div>
  );
};

export default LoginPage;
