import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../components/auth";
import axios from "axios";

const UserPage = () => {
  const { user, setUser, logout } = useContext(AuthContext);
  const [form, setForm] = useState({ name: "", password: "" });
  const [moneyToAdd, setMoneyToAdd] = useState(0);
  const [orderHistory, setOrderHistory] = useState([]);
  const [allUsers, setAllUsers] = useState([]); // solo para admin

  useEffect(() => {
    if (user) {
      setForm({ name: user.name || "", email: user.email || "", password: "" });
      fetchOrders();
      if (user.rol === "admin") fetchAllUsers();
    }
  }, [user]);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/orders/my", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrderHistory(res.data);
    } catch (err) {
      console.error("Error al obtener pedidos:", err);
    }
  };

  const fetchAllUsers = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAllUsers(res.data);
    } catch (err) {
      console.error("Error al obtener usuarios:", err);
    }
  };

  const handleProfileUpdate = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.put(
        "http://localhost:5000/api/users/me",
        { ...form },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(res.data);
      alert("Perfil actualizado ü™Ñ");
    } catch (err) {
      console.error("Error actualizando perfil:", err);
    }
  };

  const handleAddFunds = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:5000/api/users/add-funds",
        { amount: parseFloat(moneyToAdd) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(res.data);
      alert("Fondos a√±adidos ‚ú®");
    } catch (err) {
      console.error("Error al agregar fondos:", err);
    }
  };

  const handleRoleChange = async (userId, newRole) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:5000/api/users/${userId}/role`,
        { rol: newRole },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchAllUsers();
      alert("Rol actualizado üßô‚Äç‚ôÇÔ∏è");
    } catch (err) {
      console.error("Error actualizando rol:", err);
    }
  };

  if (!user) return <p>Cargando...</p>;

  return (
    <div className="profile-container">
      <h1>Perfil de {user.name}</h1>
      <section>
        <h2>Modificar informaci√≥n</h2>
        <input
          type="text"
          placeholder="Nombre"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <input
          type="email"
          placeholder="Correo"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
        />
        <input
          type="password"
          placeholder="Nueva contrase√±a"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
        />
        <button onClick={handleProfileUpdate}>Guardar cambios</button>
      </section>

      <section>
        <h2>Agregar fondos ü™ô</h2>
        <input
          type="number"
          value={moneyToAdd}
          onChange={(e) => setMoneyToAdd(e.target.value)}
          placeholder="Cantidad a agregar"
        />
        <button onClick={handleAddFunds}>Recargar</button>
      </section>

      <section>
        <h2>Historial de pedidos</h2>
        {orderHistory.length === 0 ? (
          <p>No hay pedidos todav√≠a.</p>
        ) : (
          <ul>
            {orderHistory.map((order) => (
              <li key={order._id}>
                Pedido #{order._id.slice(-5)} ‚Äì Total: {order.total} Galeones
              </li>
            ))}
          </ul>
        )}
      </section>

      {user.rol === "admin" && (
        <section>
          <h2>Administrar usuarios</h2>
          {allUsers.map((u) => (
            <div key={u._id}>
              {u.name} ({u.email}) - Rol:{" "}
              <select
                value={u.rol}
                onChange={(e) => handleRoleChange(u._id, e.target.value)}
              >
                <option value="admin">admin</option>
                <option value="usuario_magico">usuario_magico</option>
                <option value="usuario_muggle">usuario_muggle</option>
                <option value="invitado">invitado</option>
              </select>
            </div>
          ))}
        </section>
      )}
    </div>
  );
};

export default UserPage;
