import React from "react";
import "../styles/products.css";

const ProductCard = ({ product, isAuthorized, onDelete }) => {
  const priceCOP = product.priceGalleons * 5000;
  const isForbidden = product.forbidden && !isAuthorized;

  return (
    <div className="product-card">
      <img
        src={product.image}
        alt={product.name}
        className={isForbidden ? "product-image blurred" : "product-image"}
      />

      <h3>{product.name}</h3>

      <p>
        {isForbidden ? (
          <span style={{ fontStyle: "italic", color: "#a00" }}>
            üîí Producto restringido para magos autorizados
          </span>
        ) : (
          product.description
        )}
      </p>

      <p className="price">
        {product.priceGalleons}‚ö±Ô∏è &nbsp;/&nbsp;
        <span style={{ color: "#ffd700" }}>
          ${priceCOP.toLocaleString("es-CO")} COP
        </span>
      </p>

      {!isForbidden && (
        <button className="btn-add">üõí Agregar al carrito</button>
      )}

      {isAuthorized && !product.original && onDelete && (
        <button
          className="btn-add"
          style={{ marginTop: "0.5rem", backgroundColor: "#460b0b" }}
          onClick={() => onDelete(product.id)}
        >
          ‚ùå Eliminar producto
        </button>
      )}
    </div>
  );
};

export default ProductCard;
