import React, { useContext, useState } from "react";
import CartItem from "../components/CartItem";
import { AuthContext } from "../components/auth";
import axios from "axios";
import "../styles/Cart.css";
import { CartContext } from "../components/CartContext";
import { gsap } from "gsap";

const animateOrderSuccess = () => {
  const particlesCount = 30;
  const colors = ["#ffd700", "#ff8c00", "#ffffff", "#87ceeb"];

  // 🔥 Explosión mágica inicial
  for (let i = 0; i < particlesCount; i++) {
    const particle = document.createElement("div");
    particle.className = "particle";
    document.body.appendChild(particle);

    particle.style.position = "fixed";
    particle.style.left = `${window.innerWidth / 2}px`;
    particle.style.top = `${window.innerHeight / 2}px`;
    particle.style.width = "8px";
    particle.style.height = "8px";
    particle.style.borderRadius = "50%";
    particle.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
    particle.style.zIndex = "9999";
    particle.style.pointerEvents = "none";

    const angle = Math.random() * Math.PI * 2;
    const distance = 80 + Math.random() * 120;

    gsap.to(particle, {
      x: Math.cos(angle) * distance,
      y: Math.sin(angle) * distance,
      scale: 0.5,
      opacity: 0,
      duration: 1.2 + Math.random(),
      ease: "power2.out",
      onComplete: () => particle.remove(),
    });
  }

  // ✅ Pequeño "zoom mágico"
  gsap.fromTo(
    ".cart-summary-panel",
    { scale: 1 },
    { scale: 1.1, duration: 0.2, yoyo: true, repeat: 1, ease: "power1.inOut" }
  );

  // ✅ VUELO AL ÍCONO "MIS PEDIDOS"
  setTimeout(() => {
    const ordersIcon = document.querySelector("#orders-icon");
    if (!ordersIcon) return;

    const startX = window.innerWidth / 2;
    const startY = window.innerHeight / 2;
    const endRect = ordersIcon.getBoundingClientRect();

    const flyingParticle = document.createElement("div");
    flyingParticle.className = "particle";
    document.body.appendChild(flyingParticle);

    flyingParticle.style.position = "fixed";
    flyingParticle.style.left = `${startX}px`;
    flyingParticle.style.top = `${startY}px`;
    flyingParticle.style.width = "14px";
    flyingParticle.style.height = "14px";
    flyingParticle.style.borderRadius = "50%";
    flyingParticle.style.backgroundColor = "#ffd700";
    flyingParticle.style.zIndex = "9999";

    gsap.to(flyingParticle, {
      x: endRect.left - startX + 10,
      y: endRect.top - startY + 10,
      scale: 0.6,
      duration: 1.5,
      ease: "power2.inOut",
      onComplete: () => {
        flyingParticle.remove();
        // Rebote mágico en el ícono
        gsap.fromTo(
          ordersIcon,
          { scale: 1 },
          { scale: 1.3, duration: 0.3, yoyo: true, repeat: 2, ease: "power1.inOut" }
        );
      },
    });
  }, 1000); // Se lanza tras la explosión
};

const GALEON_TO_COP = 24000;

const CartPage = () => {



  const { user, setUser } = useContext(AuthContext);
  const { cartItems, removeFromCart, clearCart, updateQuantity } = useContext(CartContext);
  const [address, setAddress] = useState("");

  const toGalleons = (cop) => Math.round(cop / GALEON_TO_COP);
  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const totalCOP = total * 24000;
  const handleRemoveItem = (_id) => {
    removeFromCart(_id);
  };

  const handleQuantityChange = (_id, amount) => {
    updateQuantity(_id, amount);
  };

  const handleCheckout = async () => {
    if (!address.trim()) {
      alert("Debes ingresar una dirección de envío.");
      return;
    }

    const token = localStorage.getItem("token");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/orders",
        {
          items: cartItems,
          address,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      animateOrderSuccess();


      setTimeout(() => {
        alert("¡Pedido enviado por lechuza! 🦉");
      }, 1000); // para que no corte la animación
      clearCart();
      setAddress("");
      setUser(res.data.updatedUser);
    } catch (err) {
      console.error("Error al crear pedido:", err.response?.data || err);
      alert(err.response?.data?.error || "Error al procesar pedido.");
    }
  };

  return (
    <div className="cart-container">
      <div className="cart-items-panel">
        <h2>Productos en el carrito</h2>
        {cartItems.length === 0 ? (
          <p>Tu carrito está vacío.</p>
        ) : (
          cartItems.map((item) => (
            <CartItem
              key={item._id}
              item={item}
              toGalleons={toGalleons}
              onRemove={handleRemoveItem}
              onQuantityChange={handleQuantityChange}
            />
          ))
        )}
      </div>

      <div className="cart-summary-panel">
        <h3>Resumen de compra</h3>
        <div className="summary-row">
          <span>Total:</span>
          <span>
            💰 {total} Galeones / ${totalCOP.toLocaleString()} COP
          </span>
        </div>


        <input
          type="text"
          className="magic-input"
          placeholder="Dirección de envío (por ejemplo: Calle 9¾, Londres)"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />

        <button
          className="checkout-button"
          disabled={cartItems.length === 0}
          onClick={handleCheckout}
        >
          Realizar pedido
        </button>
      </div>
    </div>
  );
};

export default CartPage;
