import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../components/auth";
import axios from "axios";

const UserPage = () => {
  const { user, setUser, logout } = useContext(AuthContext);
  const [form, setForm] = useState({ name: "", password: "", currentPassword: "" });
  const [magicAnswer, setMagicAnswer] = useState("");
  const [orderHistory, setOrderHistory] = useState([]);
  const [allUsers, setAllUsers] = useState([]); // solo admin

  useEffect(() => {
    if (user) {
      setForm({ name: user.name || "", password: "", currentPassword: "" });
      fetchOrders();
      if (user.rol === "admin") fetchAllUsers();
    }
  }, [user]);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/orders/my", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrderHistory(res.data);
    } catch (err) {
      console.error("Error al obtener pedidos:", err);
    }
  };

  const fetchAllUsers = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAllUsers(res.data);
    } catch (err) {
      console.error("Error al obtener usuarios:", err);
    }
  };

  const handleProfileUpdate = async () => {
    if (!form.currentPassword) {
      return alert("Debes ingresar tu contraseña actual para cambiar los datos.");
    }

    try {
      const token = localStorage.getItem("token");
      const res = await axios.put(
        "http://localhost:5000/api/users/me",
        {
          name: form.name,
          password: form.password,
          currentPassword: form.currentPassword,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUser(res.data);
      alert("Perfil actualizado 🪄");
    } catch (err) {
      console.error("Error actualizando perfil:", err.response?.data || err);
      alert(err.response?.data?.error || "Error actualizando perfil");
    }
  };

  const handleAddFunds = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:5000/api/users/recharge",
        { answer: magicAnswer },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser((prev) => ({ ...prev, money: res.data.money }));
      alert("¡Recarga mágica exitosa! ✨");
    } catch (err) {
      alert("Respuesta incorrecta. Intenta con otro hechizo...");
      console.error("Error al recargar:", err.response?.data || err);
    }
  };

  const handleRoleChange = async (userId, newRole) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:5000/api/users/${userId}/role`,
        { rol: newRole },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchAllUsers();
      alert("Rol actualizado 🧙‍♂️");
    } catch (err) {
      console.error("Error actualizando rol:", err);
    }
  };

  if (!user) return <p>Cargando...</p>;

  return (
    <div className="profile-container">
      <h1>Perfil de {user.name}</h1>

      <section>
        <h2>Modificar información</h2>
        <p><strong>Usuario:</strong> {user.username}</p>
        <input
          type="text"
          placeholder="Nuevo nombre"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <input
          type="password"
          placeholder="Nueva contraseña (opcional)"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
        />
        <input
          type="password"
          placeholder="Contraseña actual (requerida)"
          value={form.currentPassword}
          onChange={(e) => setForm({ ...form, currentPassword: e.target.value })}
        />
        <button onClick={handleProfileUpdate}>Guardar cambios</button>
      </section>

      <section>
        <h2>Recarga mágica 🪙</h2>
        <p>Responde correctamente: <em>¿Qué hechizo repele a los dementores?</em></p>
        <input
          type="text"
          value={magicAnswer}
          onChange={(e) => setMagicAnswer(e.target.value)}
          placeholder="Escribe tu hechizo"
        />
        <button onClick={handleAddFunds}>Recargar</button>
      </section>

      <section>
        <h2>Historial de pedidos</h2>
        {orderHistory.length === 0 ? (
          <p>No hay pedidos todavía.</p>
        ) : (
          <ul>
            {orderHistory.map((order) => (
              <li key={order._id}>
                Pedido #{order._id.slice(-5)} – Total: {order.total} Galeones
              </li>
            ))}
          </ul>
        )}
      </section>

      {user.rol === "admin" && (
        <section>
          <h2>Administrar usuarios</h2>
          {allUsers.map((u) => (
            <div key={u._id}>
              {u.name} ({u.username}) - Rol:{" "}
              <select
                value={u.rol}
                onChange={(e) => handleRoleChange(u._id, e.target.value)}
              >
                <option value="admin">admin</option>
                <option value="usuario_magico">usuario_magico</option>
                <option value="usuario_muggle">usuario_muggle</option>
                <option value="invitado">invitado</option>
              </select>
            </div>
          ))}
        </section>
      )}
    </div>
  );
};

export default UserPage;
