import React, { useEffect, useState } from "react";
import ProductCard from "./ProductCard";
import axios from "axios";

const ProductList = ({ role }) => {
  const [items, setItems] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    category: "",
    priceGalleons: 0,
    stock: 0,
    image: "",
    forbidden: false,
  });
  const [showForm, setShowForm] = useState(false);

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/products");
      setItems(response.data);
    } catch (error) {
      console.error("Error al obtener productos", error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleDelete = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${productId}`);
      setItems((prevItems) => prevItems.filter((item) => item._id !== productId));
    } catch (error) {
      console.error("Error al eliminar producto", error);
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/products", newProduct);
      setItems((prevItems) => [...prevItems, response.data]);
      setNewProduct({
        name: "",
        description: "",
        category: "",
        priceGalleons: 0,
        stock: 0,
        image: "",
        forbidden: false,
      });
      setShowForm(false);
    } catch (error) {
      console.error("Error al agregar producto", error);
    }
  };

  return (
    <>
      {/* Mostrar formulario solo si es admin */}
      {role === "admin" && (
        <div className="product-grid">
          <div className="product-card add-product-card">
            {!showForm ? (
              <button className="btn-add" onClick={() => setShowForm(true)}>
                Agregar nuevo producto
              </button>
            ) : (
              <form onSubmit={handleAddProduct} className="form-inside-card">
                <input
                  type="text"
                  placeholder="Nombre"
                  value={newProduct.name}
                  onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                  required
                />
                <textarea
                  placeholder="Descripción"
                  value={newProduct.description}
                  onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
                  required
                />
                <input
                  type="text"
                  placeholder="Categoría"
                  value={newProduct.category}
                  onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
                  required
                />
                <input
                  type="number"
                  placeholder="Precio (Galleons)"
                  value={newProduct.priceGalleons}
                  onChange={(e) => setNewProduct({ ...newProduct, priceGalleons: e.target.value })}
                  required
                />
                <input
                  type="number"
                  placeholder="Stock"
                  value={newProduct.stock}
                  onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
                  required
                />
                <input
                  type="text"
                  placeholder="URL de imagen"
                  value={newProduct.image}
                  onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
                  required
                />
                <label>
                  <input
                    type="checkbox"
                    checked={newProduct.forbidden}
                    onChange={(e) => setNewProduct({ ...newProduct, forbidden: e.target.checked })}
                  />
                  Producto prohibido
                </label>
                <button type="submit" className="btn-add">Guardar</button>
              </form>
            )}
          </div>
        </div>
      )}

      {/* Mostrar todos los productos */}
      <div className="product-grid">
        {items.map((product) => (
          <ProductCard
            key={product._id || product.id}
            product={product}
            role={role}
            onDelete={handleDelete}
          />
        ))}
      </div>
    </>
  );
};

export default ProductList;
