import React from 'react';
import '../styles/TermsModal.css';

const TermsModal = ({ show, onClose }) => {
  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Términos y Condiciones</h2>
        <p>Al continuar usando este sitio, aceptas nuestros términos mágicos.</p>
        <p>Sortilegios no se hace responsable por explosiones inesperadas, desmayos espontáneos o revelaciones vergonzosas por caramelos de la verdad.</p>
        <button onClick={onClose}>Aceptar</button>
      </div>
    </div>
  );
};

export default TermsModal;
