  const isForbidden = product.forbidden && !isAuthorized;

  return (
    <div className="product-card">
      <img
        src={product.image}
        alt={product.name}
        className={isForbidden ? "product-image blurred" : "product-image"}
      />
      <h3>{product.name}</h3>
      <p>{isForbidden ? "üîí Producto restringido" : product.description}</p>
      <p className="price">
